import React, { Component } from 'react';
import Modal from './Modal';

export default class LoginForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            modal: false,
            name: "",
            modalInputName: ""
        };
    }

    handleChange(e) {
        const target = e.target;
        const name = target.name;
        const value = target.value;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(e) {
        this.setState({ name: this.state.modalInputName });
        this.modalClose();
    }

    modalOpen() {
        this.setState({ modal: true });
    }

    modalClose() {
        this.setState({
            modalInputName: "",
            modal: false
        });
    }

    render() {
        return (
            <div className="limiter">
                <div className="container-login100">
                    <div className="wrap-login100">
                        <form className="container login100-form">
                            <div className="row d-flex flex-column align-items-center justify-content-center">
                                <div className="wrap-input100">
                                    <label className="input100">Amount</label>
                                    <input className="input100" type="number" name="amount" />
                                </div>
                                <div className="wrap-input100">
                                    <label className="input100">Product</label>
                                    <select className="input100">
                                        <option value="personal">Personal</option>
                                        <option value="business">Business</option>
                                        <option value="unsecured">Unsecured Business Loan</option>
                                        <option value="collateral">Loan against Collateral</option>
                                        <option value="invoice">Invoice Financing</option>
                                    </select>
                                </div>
                                <div className="userInfo input100">
                                    <span>New User?</span>
                                    <span>Existing User?</span>
                                </div>
                                <div className="container-login100-form-btn">
                                    <button onClick={e => this.modalOpen(e)} type="button" className="apply login100-form-btn">Apply Now</button>
                                    <button className="login login100-form-btn">Login</button>
                                </div>
                                <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}>
                                    <div className="form-group">
                                        <label htmlFor="name">Name*</label>
                                        <input type="text" className="form-control" id="name" aria-describedby="nameHelp" placeholder="Enter Name" />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="email">Email address</label>
                                        <input type="email" className="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter email" />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="phone">Mobile No</label>
                                        <input type="tel" className="form-control" id="phone" aria-describedby="phoneHelp" placeholder="Enter Contact No" />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="password">Password</label>
                                        <input type="password" className="form-control" id="password" placeholder="Password" />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="confirmPassword">Repeat Password</label>
                                        <input type="password" className="form-control" id="confirmPassword" placeholder="Confirm Password" />
                                    </div>
                                    <button onClick={e => this.handleSubmit(e)} type="submit" className="login100-form-btn modalSubmitBtn">Submit</button>

                                    {/* <h2>Hello Modal</h2>
                                    <div className="form-group">
                                        <label>Enter Name:</label>
                                        <input
                                            type="text"
                                            value={this.state.modalInputName}
                                            name="modalInputName"
                                            onChange={e => this.handleChange(e)}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <button onClick={e => this.handleSubmit(e)} type="button">
                                            Save
                                        </button>
                                    </div> */}
                                </Modal>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
