import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

export default class LoginForm extends Component {
    constuctor() {
        this.routeChange = this.routeChange.bind(this);
    }

    routeChange() {
        let path = `newPath`;
        this.props.history.push(path);
    }

    render() {
        return (
            <div className="limiter">
                <div className="container-login100">
                    <div className="wrap-login100">
                        <form className="container login100-form">
                            <div className="row d-flex flex-column align-items-center justify-content-center">
                                <div className="wrap-input100">
                                    <label className="input100">Amount</label>
                                    <input className="input100" type="number" name="amount" />
                                </div>
                                <div className="wrap-input100">
                                    <label className="input100">Product</label>
                                    <select className="input100">
                                        <option value="personal">Personal</option>
                                        <option value="business">Business</option>
                                        <option value="unsecured">Unsecured Business Loan</option>
                                        <option value="collateral">Loan against Collateral</option>
                                        <option value="invoice">Invoice Financing</option>
                                    </select>
                                </div>
                                <div className="userInfo input100">
                                    <span>New User?</span>
                                    <span>Existing User?</span>
                                </div>
                                <div className="container-login100-form-btn">
                                    <button onClick={this.routeChange} type="button" className="apply login100-form-btn">Apply Now</button>
                                    <button className="login login100-form-btn">Login</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
